import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import logo from './images/LogoLoQueMastica.png'
import titulo from './images/Titulo.png'
import './css/Header.css';

function Header(props) {
    const [showSecondHeader, setShowSecondHeader] = useState(false);

    const handleScroll = () => {
        if (window.scrollY > 50) { // Cambia el número 100 según tu necesidad
            setShowSecondHeader(true);
        } else {
            setShowSecondHeader(false);
        }
    }

    // Registra el evento de scroll al montar el componente
    React.useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []); // No olvides especificar un arreglo de dependencias vacío para que se ejecute solo al montar el componente

    return (
        <>
            {showSecondHeader ? (
                <header className="second-header">
                    <button className="second-header-button">Inicio</button>
                    <div className="buttons-container">
                        <button className="header-button">MARCAS</button>
                        <button className="header-button">SUCURSALES</button>
                        <button className="header-button">TIENDA</button>
                        <button className="header-button">MI CUENTA</button>
                    </div>
                </header>
            ) : (
                <header className="header">
                    <div className="logo-container header-title">
                        <img src={titulo} alt="Título principal de mi sitio web" />
                    </div>
                    <div className="buttons-container">
                        <button className="header-button">MARCAS</button>
                        <button className="header-button">SUCURSALES</button>
                        <button className="header-button">TIENDA</button>
                        <button className="header-button">MI CUENTA</button>
                    </div>
                    <div className="cart-container">
                        <button className="cart-button">
                            <FontAwesomeIcon icon={faShoppingCart} className="cart-icon" />
                            <span className="cart-count">{props.cartCount}</span>
                        </button>
                    </div>
                </header>
            )}
        </>
    );
}

export default Header;
