                await axios.delete(`${urlElminarProducto}/${id}`);
                Swal.fire({
                    title: 'Producto eliminado',
                    text: 'El producto se eliminó correctamente',
                    icon: 'success',
                    confirmButtonText: 'Aceptar'

                }
                ).then((result) => {
                    if (result.isConfirmed) {
                        getProductos();
                    }
                });
            }
        } catch (error) {
            console.log(error);
        }
