import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const urlCrearProducto = "http://localhost:3000/crearProducto";
const urlListadoCategoria = "http://localhost:3000/getCategorias";

const CrearProducto = () => {
    const [NOMBRE, SETNOMBRE] = useState("");
    const [DESCRIPCION, SETDESCRIPCION] = useState("");
    const [PRECIO, SETPRECIO] = useState("");
    const [STOCK, SETSTOCK] = useState("");
    const [Id_Categoria, SETIDCATEGORIA] = useState("");
    const navigate = useNavigate();

    //procedimiento para guardar los datos del formulario
    const guardarDatos = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post(urlCrearProducto, {
                NOMBRE,
                DESCRIPCION,
                PRECIO,
                STOCK,
                Id_Categoria,
            });
            console.log(res);
            navigate("/getProductos");
        } catch (error) {
            console.log(error);
        }
    }


    return (
        <div>
            <h3>Crear Producto</h3>
            <form onSubmit={guardarDatos}>
                <div className="form-group mb-3">
                    <label className="form-label">Nombre</label>
                    <input type="text" className="form-control" onChange={(e) => SETNOMBRE(e.target.value)} /> {/*guardo el valor del input en el estado, por eso llamo a setnombre para setear el valor, el (e) es de evento*/}
                </div>
                <div className="form-group mb-3">
                    <label className="form-label">Descripcion</label>
                    <input type="text" className="form-control" onChange={(e) => SETDESCRIPCION(e.target.value)} />
                </div>
                <div className="form-group mb-3">
                    <label className="form-label">Precio</label>
                    <input type="text" className="form-control" onChange={(e) => SETPRECIO(e.target.value)} />
                </div>
                <div className="form-group mb-3">
                    <label className="form-label">Stock</label>
                    <input type="text" className="form-control" onChange={(e) => SETSTOCK(e.target.value)} />
                </div>
                <div className="form-group mb-3">
                    <label className="form-label">Categoria</label>
                    <select className="form-select" onChange={(e) => SETIDCATEGORIA(e.target.value)}>
                        <option value="0">Seleccione una categoria</option>
                        <option value="1">Perros</option>
                        <option value="2">Gatos</option>
                        <option value="3">Aves</option>
                        <option value="4">Roedores</option>
                        <option value="5">Peces</option>
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Guardar</button>

            </form>
        </div>
    )





}

export default CrearProducto;
