import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';


const urlCrearProducto = process.env.REACT_APP_API_URL + "/crearProducto";
const urlListadoCategoria = process.env.REACT_APP_API_URL + "/getCategorias";
const urlCargarImagen = process.env.REACT_APP_API_URL + "/uploadFile";


const CrearProducto = () => {
    const [Nombre, SetNombre] = useState("");
    const [Descripcion, SetDescripcion] = useState("");
    const [Precio, SetPrecio] = useState("");
    const [Stock, SetStock] = useState("");
    const [Id_Categoria, SetIdCategoria] = useState("");
    const [Url_Imagen, SetImagen] = useState("");
    const navigate = useNavigate();

    const [categorias, setCategorias] = useState([]);
    useEffect(() => {
        getCategorias();
    }, [])

    //funcion para mostrar todos los productos
    const getCategorias = async () => {
        debugger;
        try {
            const res = await axios.get(urlListadoCategoria);
            setCategorias(res.data); //actualizo el estado del componente con los datos que me devuelve la api
            // console.log(res.data);

        } catch (error) {
            console.log(error);
        }

    }


    //procedimiento para guardar los datos del formulario
    const guardarDatos = async (e) => {

        debugger;
        e.preventDefault();

        try {
            if (!Nombre) {
                Swal.fire({
                    position: 'center',
                    icon: 'error',
                    title: 'Ingrese un nombre',
                    showConfirmButton: true,

                })
                return;
            }

            //CON ESTO LOGRE HACER QUE SE GUARDE LA IMAGEN EN LA CARPETA UPLOADS Y EL NOMBRE EN LA BASE DE DATOS
            try {
                const formData = new FormData();
                formData.append("file", Url_Imagen);

                const respuesta = await axios.post(urlCargarImagen, formData);
                console.log(respuesta);
                var url = respuesta.data;

                if (url == null) {
                    throw "Error al cargar la imagen";
                }
                debugger;

            } catch (error) {
                console.log(error);
                throw error;
            }

            try {
                const res = await axios.post(urlCrearProducto, {
                    Nombre,
                    Descripcion,
                    Precio,
                    Stock,
                    Id_Categoria,
                    Url_Imagen: url
                });
                console.log(res);
                Swal.fire({
                    position: 'center',
                    icon: 'success',
                    backdrop: false,
                    title: 'Producto creado correctamente',
                    showConfirmButton: true,

                }).then((result) => {
                    if (result.isConfirmed) {
                        navigate("/");
                    }
                })


            } catch (error) {
                throw error;
            }

        } catch (error) {
            console.log(error);
        }
    }


    return (
        <div className="card mt-5">

            <div className="card-header d-flex justify-content-between align-items-center">
                <h3 className="mt-3 mb-1">Crear Producto</h3>
                <div className="mt-1 mb-1">
                    <Link to="/" className="btn btn-primary">Volver</Link>
                </div>
            </div>
            <div className="formulario">
                <div className="card-body">

                    <form id="formProducto" onSubmit={guardarDatos} className="card-body">
                        <div className="form-group mb-3 w-33">
                            <label>Nombre</label>
                            <input placeholder="Ingrese un Nombre" type="text" className="form-control" onChange={(e) => SetNombre(e.target.value)} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Descripcion</label>
                            <input placeholder="Ingrese una Descripcion" type="text" className="form-control" onChange={(e) => SetDescripcion(e.target.value)} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Precio</label>
                            <input placeholder="Ingrese un precio" type="text" className="form-control" onChange={(e) => SetPrecio(parseFloat(e.target.value))} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Stock</label>
                            <input placeholder="Ingrese un Stock" type="text" className="form-control" onChange={(e) => SetStock(parseInt(e.target.value))} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Categoria</label>
                            <select className="form-select selectize" onChange={(e) => SetIdCategoria(parseInt(e.target.value))}>
                                <option value="" className="placeholder-option" disabled selected>Seleccione una categoria</option>
                                {
                                    categorias.map((categoria) => (
                                        <option key={categoria.Id} value={categoria.Id}>{categoria.Nombre}</option>
                                    ))
                                }
                            </select>

                        </div>

                        <div className="form-group mb-3 w-33 mt-4">
                            <div className="mt-3">
                                <label className="form-label">Imagen</label>
                                <div className="input-group mb-4">
                                    <input type="file" className="form-control" id="inputGroupFile04" onChange={(e) => SetImagen(e.target.files[0])} aria-describedby="inputGroupFileAddon04" aria-label="Upload" />
                                </div>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary center-button">Guardar</button>
                    </form>
                </div>

            </div>





            <form class="row g-3 needs-validation" novalidate>
                <div class="col-md-4">
                    <label for="validationCustom01" class="form-label">First name</label>
                    <input type="text" class="form-control" id="validationCustom01" value="Mark" required>
                        <div class="valid-feedback">
                            Looks good!
                        </div>
                    </input>
                </div>
                <div class="col-md-4">
                    <label for="validationCustom02" class="form-label">Last name</label>
                    <input type="text" class="form-control" id="validationCustom02" value="Otto" required>
                        <div class="valid-feedback">
                            Looks good!
                        </div>
                    </input>
                </div>
                <div class="col-md-4">
                    <label for="validationCustomUsername" class="form-label">Username</label>
                    <div class="input-group has-validation">
                        <span class="input-group-text" id="inputGroupPrepend">@</span>
                        <input type="text" class="form-control" id="validationCustomUsername" aria-describedby="inputGroupPrepend" required>
                            <div class="invalid-feedback">
                                Please choose a username.
                            </div>
                        </input>
                    </div>
                </div>
                <div class="col-md-6">
                    <label for="validationCustom03" class="form-label">City</label>
                    <input type="text" class="form-control" id="validationCustom03" required>
                        <div class="invalid-feedback">
                            Please provide a valid city.
                        </div>
                    </input>
                </div>
                <div class="col-md-3">
                    <label for="validationCustom04" class="form-label">State</label>
                    <select class="form-select" id="validationCustom04" required>
                        <option selected disabled value="">Choose...</option>
                        <option>...</option>
                    </select>
                    <div class="invalid-feedback">
                        Please select a valid state.
                    </div>
                </div>
                <div class="col-md-3">
                    <label for="validationCustom05" class="form-label">Zip</label>
                    <input type="text" class="form-control" id="validationCustom05" required>
                    </input>
                    <div class="invalid-feedback">
                        Please provide a valid zip.
                    </div>
                </div>
                <div class="col-12">
                    <div class="form-check">
                        <input class="form-check-input" type="checkbox" value="" id="invalidCheck" required>
                            <label class="form-check-label" for="invalidCheck">
                                Agree to terms and conditions
                            </label>
                            <div class="invalid-feedback">
                                You must agree before submitting.
                            </div>
                        </input>
                    </div>
                </div>
                <div class="col-12">
                    <button class="btn btn-primary" type="submit">Submit form</button>
                </div>
            </form>













        </div>





    )





