import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';

const urlCrearProducto = "http://localhost:3000/crearProducto";
const urlListadoCategoria = "http://localhost:3000/getCategorias";

const CrearProducto = () => {
    const [Nombre, SetNombre] = useState("");
    const [Descripcion, SetDescripcion] = useState("");
    const [Precio, SetPrecio] = useState("");
    const [Stock, SetStock] = useState("");
    const [Id_Categoria, SetIdCategoria] = useState("");
    const navigate = useNavigate();

    const [categorias, setCategorias] = useState([]);
    useEffect(() => {
        getCategorias();
    }, [])

    //funcion para mostrar todos los productos
    // const getCategorias = async () => {
    //     debugger;
    //     try {



    //         const res = await axios.get(urlListadoCategoria);
    //         setCategorias(res.data); //actualizo el estado del componente con los datos que me devuelve la api
    //         console.log(res.data);

    //     } catch (error) {
    //         console.log(error);
    //     }

    // }

    function getCategorias() {
        debugger;
        fetch('http://localhost:3000/getCategorias')
          .then(response => response.json())
          .then(data => console.log(data))
          .catch(error => console.error(error))
      }


    useEffect(() => {
        try {
            const categorias = async () => {
                const res = await axios.get(urlListadoCategoria);
                setCategorias(res.data);
            }

        } catch (error) {
            console.log(error);
        }
    })




    //procedimiento para guardar los datos del formulario
    const guardarDatos = async (e) => {

        debugger;
        e.preventDefault();
        try {
            //envio los datos al backend para que los guarde en la base de datos
            //TIENE QUE TENER EL MISMO NAME QUE EL MODELO, SINO EL BACK NO LO ENTIENDE
            const res = await axios.post(urlCrearProducto, {
                Nombre,
                Descripcion,
                Precio,
                Stock,
                Id_Categoria,
            });
            console.log(res);
            Swal.fire({
                position: 'center',
                icon: 'success',
                title: 'Producto creado correctamente',
                showConfirmButton: true,

            }).then((result) => {
                if (result.isConfirmed) {
                    navigate("/productos");
                }
            })
            // navigate("/productos");
        } catch (error) {
            console.log(error);
        }
    }


    return (
        <div className="card">
            <div className="card-header">
                <h3>Crear Producto</h3>
            </div>
            <div className="formulario">
                <form id="formProducto" onSubmit={guardarDatos} className="card-body">
                    <div className="form-group mb-3 w-33">
                        <label>Nombre</label>
                        <input type="text" className="form-control" onChange={(e) => SetNombre(e.target.value)} />
                    </div>
                    <div className="form-group mb-3 w-33">
                        <label className="form-label">Descripcion</label>
                        <input type="text" className="form-control" onChange={(e) => SetDescripcion(e.target.value)} />
                    </div>
                    <div className="form-group mb-3 w-33">
                        <label className="form-label">Precio</label>
                        <input type="number" className="form-control" onChange={(e) => SetPrecio(parseFloat(e.target.value))} />
                    </div>
                    <div className="form-group mb-3 w-33">
                        <label className="form-label">Stock</label>
                        <input type="number" className="form-control" onChange={(e) => SetStock(parseInt(e.target.value))} />
                    </div>
                    <div className="form-group mb-3 w-33">
                        <label className="form-label">Categoria</label>
                        <select className="form-select" onChange={(e) => SetIdCategoria(parseInt(e.target.value))}>
                            <option value="">Seleccione una categoria</option>
                            {
                                categorias.map((categoria) => (
                                    <option key={categoria.Id} value={categoria.Id}>{categoria.Nombre}</option>
                                ))
                            }
                        </select>
                    </div>
                    <button type="submit" className="btn btn-primary">Guardar</button>
                </form>


            </div>
        </div>
    )





}

export default CrearProducto;
