    //                             <th>Nombre Categoria</th>
    //                             <th>Descripcion Categoria</th>
    //                             <th>Url</th>
    //                             <th>Acciones</th>
    //                         </tr>
    //                     </thead>
    //                     <tbody>
    //                         {
    //                             productos.map((producto) => (
    //                                 <tr key={producto.Id}>
    //                                     <td>{producto.Nombre}</td>
    //                                     <td>{producto.Descripcion}</td>
    //                                     <td>{producto.Precio}</td>
    //                                     <td>{producto.Stock}</td>
    //                                     <td>{producto.Categoria.Nombre}</td>
    //                                     <td>{producto.Categoria.Descripcion}</td>
    //                                     <td>{producto.Url_Imagen}</td>
    //                                     <td style={{ display: 'flex', gap: '10px' }}>
    //                                         <Link to={`/editarProducto/${producto.Id}`} className='btn btn-info'><i className="fa-solid fa-pen-to-square"></i></Link>
    //                                         <button className='btn btn-danger' onClick={() => deleteProducto(producto.Id)}><i className="fa-sharp fa-solid fa-trash"></i></button>
    //                                     </td>


    //                                 </tr>
    //                             )

    //                             )
    //                         }
    //                     </tbody>
    //                 </table>
    //             </div>
    //         </div>


    //     </div>
    // )


    return (
        <div className='productos-container'>
          {productos.map((producto) => (
            <div className='producto-card' key={producto.Id}>
              <div className='producto-image-container'>
                <img className='producto-image' src={producto.ImagenUrl} alt={producto.Nombre} />
              </div>
              <div className='producto-info-container'>
                <h2 className='producto-nombre'>{producto.Nombre}</h2>
                <p className='producto-descripcion'>{producto.Descripcion}</p>
                <p className='producto-precio'>Precio: {producto.Precio}</p>
                <p className='producto-stock'>Stock: {producto.Stock}</p>
                <p className='producto-categoria'>Categoría: {producto.Categoria.Nombre}</p>
                <div className='producto-acciones'>
                  <Link to={`/editarProducto/${producto.Id}`} className='btn btn-info'>
                    <i className="fa-solid fa-pen-to-square"></i> Editar
                  </Link>
                  <button className='btn btn-danger' onClick={() => deleteProducto(producto.Id)}>
                    <i className="fa-sharp fa-solid fa-trash"></i> Eliminar
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      );


}

