import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';

const urlCrearProducto = "http://localhost:3000/crearProducto";
const urlListadoCategoria = "http://localhost:3000/getCategorias";

const CrearProducto = () => {
    const [Nombre, SetNombre] = useState("");
    const [Descripcion, SetDescripcion] = useState("");
    const [Precio, SetPrecio] = useState("");
    const [Stock, SetStock] = useState("");
    const [Id_Categoria, SetIdCategoria] = useState("");
    const [Url_Imagen, SetImagen] = useState("");
    const navigate = useNavigate();

    const [categorias, setCategorias] = useState([]);
    useEffect(() => {
        getCategorias();
    }, [])

    //funcion para mostrar todos los productos
    const getCategorias = async () => {
        debugger;
        try {
            const res = await axios.get(urlListadoCategoria);
            setCategorias(res.data); //actualizo el estado del componente con los datos que me devuelve la api
            // console.log(res.data);

        } catch (error) {
            console.log(error);
        }

    }




    //procedimiento para guardar los datos del formulario
    const guardarDatos = async (e) => {

        debugger;
        e.preventDefault();
        console.log(Url_Imagen)
        try {
            if (!Nombre) {
                Swal.fire({
                    position: 'center',
                    icon: 'error',
                    title: 'Ingrese un nombre',
                    showConfirmButton: true,

                })
                return;
            }

            const reader = new FileReader(); //creo un objeto de la clase FileReader
            reader.readAsDataURL(Url_Imagen); //convierto la imagen a base64 para poder enviarla al backend
            reader.onload = async () => { //cuando termina de convertir la imagen a base64
                const data = new Blob([new Uint8Array(reader.result)], { type: file.type }); // crear un blob con los datos del archivo

                const config = {
                    headers: {
                        'content-type': file.type, // establecer el tipo de contenido del archivo
                    },
                };
                //envio los datos al backend para que los guarde en la base de datos
                //TIENE QUE TENER EL MISMO NAME QUE EL MODELO, SINO EL BACK NO LO ENTIENDE
                const res = await axios.post(urlCrearProducto, data, config, {
                    Nombre,
                    Descripcion,
                    Precio,
                    Stock,
                    Id_Categoria,
                    Url_Imagen
                });
            }
            console.log(res);
            Swal.fire({
                position: 'center',
                icon: 'success',
                title: 'Producto creado correctamente',
                showConfirmButton: true,

            }).then((result) => {
                if (result.isConfirmed) {
                    navigate("/");
                }
            })
            // navigate("/productos");
        } catch (error) {
            console.log(error);
        }
    }


    return (
        <div className="card mt-5">

            <div className="card-header d-flex justify-content-between align-items-center">
                <h3 className="mt-3 mb-1">Crear Producto</h3>
                <div className="mt-1 mb-1">
                    <Link to="/" className="btn btn-primary">Volver</Link>
                </div>
            </div>
            <div className="formulario">
                <div className="card-body">

                    <form id="formProducto" onSubmit={guardarDatos} className="card-body">
                        <div className="form-group mb-3 w-33">
                            <label>Nombre</label>
                            <input placeholder="Ingrese un Nombre" type="text" className="form-control" onChange={(e) => SetNombre(e.target.value)} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Descripcion</label>
                            <input placeholder="Ingrese una Descripcion" type="text" className="form-control" onChange={(e) => SetDescripcion(e.target.value)} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Precio</label>
                            <input placeholder="Ingrese un precio" type="text" className="form-control" onChange={(e) => SetPrecio(parseFloat(e.target.value))} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Stock</label>
                            <input placeholder="Ingrese un Stock" type="text" className="form-control" onChange={(e) => SetStock(parseInt(e.target.value))} />
                        </div>
                        <div className="form-group mb-3 w-33">
                            <label className="form-label">Categoria</label>
                            <select className="form-select selectize" onChange={(e) => SetIdCategoria(parseInt(e.target.value))}>
                                <option value="" className="placeholder-option" disabled selected>Seleccione una categoria</option>
                                {
                                    categorias.map((categoria) => (
                                        <option key={categoria.Id} value={categoria.Id}>{categoria.Nombre}</option>
                                    ))
                                }
                            </select>

                        </div>
                        <div className="form-group mb-3 w-33 mt-4">
                            <div className="card mt-3">
                                <label className="form-label">Imagen</label>
                                <input onChange={(e) => SetImagen(e.target.files[0])} type="file" className="form-control" />
                                <button className="btn btn-primary btn-sm">
                                    <i className="fas fa-upload me-2"></i>
                                    Subir
                                </button>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary center-button">Guardar</button>
                    </form>
                </div>

            </div>
        </div>
    )





}

export default CrearProducto;
